package arrays;

public class Main {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		String [] meyveler = {"Elma", "Armut", "Portakal", "Muz"};
		int meyveSayisi = meyveler.length;
		System.out.println("Meyve Sayisi: " + meyveSayisi);
		System.out.println("1. Meyve: " + meyveler[0]);
		System.out.println("2. Meyve: " + meyveler[1]);
		System.out.println("3. Meyve: " + meyveler[2]);
		System.out.println("4. Meyve: " + meyveler[3]);
	}

}
