package if_else;

public class Main {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int sayi = 10;
		if (sayi < 0) {
			System.out.println("Sayi negatif");
		}else if (sayi > 0) {
			System.out.println("Sayi pozitif");
		}else{
			System.out.println("Sayi 0'a esittir");
		
		}
	}

}
